import React, { useState, useEffect, useRef } from 'react';
import { Grid, Typography, Button, TextField } from '@mui/material';
import Chart from 'chart.js/auto';
import axios from 'axios';

function Stats({ playerName, game }) {
    const [teamMembers, setTeamMembers] = useState([{ name: '', wins: 0, losses: 0 }]);
    const [winLossRatio, setWinLossRatio] = useState([]);
    const chartRef = useRef(null);

    const handleNameChange = (index, event) => {
        const updatedTeamMembers = [...teamMembers];
        updatedTeamMembers[index].name = event.target.value;
        setTeamMembers(updatedTeamMembers);
    };

    const handleWinsChange = (index, event) => {
        const updatedTeamMembers = [...teamMembers];
        updatedTeamMembers[index].wins = Number(event.target.value);
        setTeamMembers(updatedTeamMembers);
    };

    const handleLossesChange = (index, event) => {
        const updatedTeamMembers = [...teamMembers];
        updatedTeamMembers[index].losses = Number(event.target.value);
        setTeamMembers(updatedTeamMembers);
    };

    const handleAddMember = () => {
        setTeamMembers([...teamMembers, { name: '', wins: 0, losses: 0 }]);
    };

    useEffect(() => {
        // Calculate win-loss ratio for each team member
        const ratios = teamMembers.map(member => member.wins / (member.wins + member.losses));
        setWinLossRatio(ratios);
    }, [teamMembers]);

    useEffect(() => {
        // Update chart
        if (chartRef.current) {
            const ctx = chartRef.current.getContext('2d');
            new Chart(ctx, {
                type: 'bar',
                data: {
                    labels: teamMembers.map(member => member.name),
                    datasets: [{
                        label: 'Win vs Loss Ratio',
                        data: winLossRatio,
                        backgroundColor: 'rgba(54, 162, 235, 0.2)',
                        borderColor: 'rgba(54, 162, 235, 1)',
                        borderWidth: 1
                    }]
                },
                options: {
                    scales: {
                        y: {
                            beginAtZero: true
                        }
                    }
                }
            });
        }
    }, [teamMembers, winLossRatio]);

    return (
        <Grid container direction="column" alignItems="center" className="stats">
            <Typography variant="h3">{playerName}</Typography>
            <Typography variant="body1">Game: {game}</Typography>
            {/* Input fields for team members */}
            {teamMembers.map((member, index) => (
                <div key={index}>
                    <TextField
                        type="text"
                        label={`Name ${index + 1}`}
                        value={member.name}
                        onChange={(event) => handleNameChange(index, event)}
                        variant="outlined"
                        style={{ marginBottom: '8px' }}
                    />
                    <TextField
                        type="number"
                        label="Wins"
                        value={member.wins}
                        onChange={(event) => handleWinsChange(index, event)}
                        variant="outlined"
                        style={{ marginBottom: '8px' }}
                    />
                    <TextField
                        type="number"
                        label="Losses"
                        value={member.losses}
                        onChange={(event) => handleLossesChange(index, event)}
                        variant="outlined"
                        style={{ marginBottom: '16px' }}
                    />
                </div>
            ))}
            {/* Button to add more team members */}
            <Button variant="outlined" color="primary" onClick={handleAddMember}>Add Team Member</Button>
            {/* Canvas for the chart */}
            <canvas ref={chartRef} width="400" height="200"></canvas>
        </Grid>
    );
}

export default Stats;
